import { movies } from "../../movies";
import {
  NEXT,
  PREV,
  REMOVE_MOVIE,
  RESTORE_MOVIE,
} from "../actions/movieAction";

const initialState = {
  movies: movies,
  sira: 0,
};

function movieReducer(state = initialState, action) {
  switch (action.type) {
    case NEXT:
      return { ...state, sira: state.sira + 1 };
    default:
      return state;
  }
}
export default movieReducer;
